configs.viteConfigRaw:
import { defineConfig } from "vite";
import react from '@vitejs/plugin-react';

export default defineConfig({
  plugins: [react()],
  base: '/',
  build: { 
    outDir: 'dist', 
    sourcemap: false, 
    emptyOutDir: true,
    rollupOptions: {
      output: {
        manualChunks: undefined
      }
    }
  },
  server: {
    port: 3000,
    host: true
  }
  // No CSS config - let Vite handle CSS natively without PostCSS
});

configs.devcontainerRaw:
{
  "name": "elevate-for-humanity",
  "image": "mcr.microsoft.com/devcontainers/javascript-node:18",
  "hostRequirements": { "cpus": 4, "memory": "8gb", "storage": "32gb" },
  "forwardPorts": [8080, 3000, 5173],
  "portsAttributes": {
    "8080": {
      "label": "Main Site",
      "onAutoForward": "openPreview"
    },
    "3000": {
      "label": "Development Server",
      "onAutoForward": "openPreview"
    }
  },
  "postCreateCommand": "npm install -g http-server && echo 'Dev container ready'",
  "customizations": {
    "vscode": {
      "settings": {
        "terminal.integrated.defaultProfile.linux": "bash",
        "terminal.integrated.profiles.linux": {
          "bash": { "path": "/bin/bash", "args": ["-l"] }
        },
        "files.watcherExclude": {
          "**/node_modules/**": true,
          "**/.git/**": true,
          "**/dist/**": true
        }
      },
      "extensions": [
        "esbenp.prettier-vscode",
        "ms-vscode.vscode-json",
        "bradlc.vscode-tailwindcss"
      ]
    }
  },
  "features": {
    "ghcr.io/devcontainers/features/git:1": {},
    "ghcr.io/devcontainers/features/github-cli:1": {}
  }
}
